var input = require('readline-sync');
var income = input.questionInt("Enter your annual income : ");
var status = input.question("Enter your filing status:\na - Single filer\nb - Married Filing Jointly\n> ");
var tax=0;
if (status=='a') {
    if (income>6000 && income<20000) {
        tax += 60000*0.1;
        income = income -6000;
        tax += income*0.15;
    }

    else if (income>20000) {
        tax += 20000*0.135;
        income = income - 20000;
        tax += income*0.27;
    }

    else if (income == 20000){
        tax += 2700;
    }
    else {
        tax += income*0.1;
    }
    console.log("Your tax amount is $" + tax);

}

else if (status == 'b') {
if (income>12000&&income<38000) {
    tax+= 12000*0.1;
    income = income -12000;
    tax += income*0.15;
}

else if (income>38000) {
    tax += 5100;
    income = income - 38000;
    tax += income *0.27;
}
else if (income==380000) {
    tax += 5100;
}
else{
    tax += income *0.1;
}
console.log("Your tax amount is $" + tax);
}
else {
    console.log("Invalid Filing Status.\nProgram Terminated...");
}

