var eyear, syear;
function readYear() {
   
    var input= require ('readline-sync');
    syear = input.question("Enter Start Year: ");
    eyear = input.question("Enter End Year: ");
    
}
function isLeapYear(year) {
    for (var i=syear; i<eyear; i++) {
        if (((year % 4 == 0) && !(year % 100 == 0))||(year % 400 == 0)) {
            year += " "
        }
        else year += " not"
        console.log(i + " is " + year + " a leap year ");
    }
    
}
readYear(syear, eyear);
isLeapYear();


