class MCQ {
    constructor(question, choice1, choice2, choice3, choice4, answer, category) {
        this.question = question;
        this.choice = [choice1, choice2, choice3, choice4];
        this.answer = answer;
        this.category = category;
    }

    getQuestion() {
        var s = console.log(this.question);
        
        for (var i = 0; i < this.choice.length; i++) {
            console.log(this.choice[i]);
            break;
        }
        return s;
    }

} //class mcq


